import React, { useState } from 'react';
import { TaskStatus, Task } from '../lib/interfaces/ITask.interface';
import { TaskDBRes } from '../App';

interface Props extends Task {
  deleteTask: (id: string) => void;
  consoleThatOne: (id: string) => void;
  updateTask: (id: string, status: TaskStatus | string) => void;
}

const TaskComponent = ({
  deleteTask,
  status,
  updateTask,
  consoleThatOne,
  title,
  description,
  id,
}: Props) => {
  const [statusState, setStatusState] = useState<TaskStatus | string>(status);
  const [inputValid, setInputValid] = useState<boolean>(false);

  console.log(TaskStatus);

  async function change(
    e: React.ChangeEvent<HTMLSelectElement>,
  ): Promise<void> {
    setStatusState(e.target.value);
    const res: TaskDBRes = await updateTask(id, e.target.value);
    setStatusState(res.changed);
  }

  return (
    <div>
      <p>{title}</p>
      <p>{description}</p>
      <button onClick={() => consoleThatOne(id)}>Click for console</button>
      <br />
      <button onClick={() => deleteTask(id)}>Delete Task</button>
      <br />
      <button onClick={() => setInputValid((c) => !c)}>Update this task</button>
      {inputValid && (
        <select
          onChange={async (e: React.ChangeEvent<HTMLSelectElement>) => {
            setStatusState(e.target.value);

            const res: TaskDBRes = await updateTask(id, e.target.value);
            console.log('res:', res);
            setStatusState(res.changed);
          }}
        >
          {Object.keys(TaskStatus).map((el: any, i: number) => (
            <option value={el} key={el}>
              {el}
            </option>
          ))}
        </select>
      )}
    </div>
  );
};

export default TaskComponent;
